package decodemorseadvanced

import "testing"

func TestDecodeMorse(t *testing.T) {
	type args struct {
		morseCode string
	}
	tests := []struct {
		name string
		args args
		want string
	}{
		{"basic", args{morseCode: "1"}, "E"},
		{"basic", args{morseCode: "101"}, "I"},
		{"basic", args{morseCode: "10001"}, "EE"},
		{"basic", args{morseCode: "10111"}, "A"},
		{"basic", args{morseCode: "1110111"}, "M"},
		{"multiple bits", args{morseCode: "111"}, "E"},
		{"multiple bits", args{morseCode: "1111111"}, "E"},
		{"multiple bits", args{morseCode: "110011"}, "I"},
		{"multiple bits", args{morseCode: "111000111"}, "I"},
		{"multiple bits", args{morseCode: "111110000011111"}, "I"},
		{"multiple bits", args{morseCode: "11111100111111"}, "M"},
		{"multiple bits", args{morseCode: "111000000000111"}, "EE"},
		{"multiple bits", args{morseCode: "111000111000111"}, "S"},
		{"multiple bits", args{morseCode: ""}, "HEY JUDE"},
		{"extra zeros", args{morseCode: "01110"}, "E"},
		{"extra zeros", args{morseCode: "000000011100000"}, "E"},
		{"long message", args{morseCode: ""}, "THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG."},
		{"long message", args{morseCode: ""}, "THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG."},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := DecodeMorse(DecodeBits(tt.args.morseCode)); got != tt.want {
				t.Errorf("DecodeMorse() = %v, want %v", got, tt.want)
			}
		})
	}
}
